# RUN_STEP: <elaborate>
elaborate()
{
  opts="-64bit -relax -access +rwc -messages -logfile elaborate.log -timescale 1ps/1ps"
  libs="-libname unisims_ver -libname unimacro_ver -libname secureip -libname xil_defaultlib -libname microblaze_v9_5_3 -libname kth_axi_rni_static_v1_00_a -libname proc_common_v3_00_a -libname axi_lite_ipif_v1_01_a -libname axi_lite_ipif_v3_0_3 -libname lib_cdc_v1_0_2 -libname interrupt_control_v3_1_3 -libname axi_gpio_v2_0_9 -libname lib_pkg_v1_0_2 -libname axi_timer_v2_0_8 -libname lmb_v10_v3_0_7 -libname lmb_bram_if_cntlr_v4_0_7 -libname blk_mem_gen_v8_3_1 -libname mdm_v3_2_4 -libname proc_sys_reset_v5_0_8 -libname generic_baseblocks_v2_1_0 -libname axi_infrastructure_v1_1_0 -libname axi_register_slice_v2_1_7 -libname fifo_generator_v13_0_1 -libname axi_data_fifo_v2_1_6 -libname axi_crossbar_v2_1_8 -libname util_vector_logic_v2_0 -libname kth_2dnoc_2x2x1_v1_00_a"
  ncelab $opts xil_defaultlib.BD xil_defaultlib.glbl $libs
}

# RUN_STEP: <simulate>
simulate()
{
  opts="-64bit -logfile simulate.log"
  ncsim $opts xil_defaultlib.BD -input simulate.do
}
# Script usage
usage()
{
  msg="Usage: BD.sh [-help]\n\
Usage: BD.sh [-lib_map_path]\n\
Usage: BD.sh [-reset_run]\n\
Usage: BD.sh [-noclean_files]\n\n\
[-help] -- Print help information for this script\n\n\
[-lib_map_path <path>] -- Compiled simulation library directory path. The simulation library is compiled\n\
using the compile_simlib tcl command. Please see 'compile_simlib -help' for more information.\n\n\
[-reset_run] -- Recreate simulator setup files and library mappings for a clean run. The generated files\n\
from the previous run will be removed. If you don't want to remove the simulator generated files, use the\n\
-noclean_files switch.\n\n\
[-noclean_files] -- Reset previous run, but do not remove simulator generated files from the previous run.\n\n"
  echo -e $msg
  exit 1
}


# Launch script
run $1 $2
